<script>
        // Respuestas correctas
        const respuestasCorrectas = {
            p1: 'b',  // 6 caras
            p2: 'v',  // Verdadero
            p3: 'b',  // Esfera
            p4: 'f',  // Falso (eso son las caras, no las aristas)
            p5: 'b',  // 5 v√©rtices
            p6: 'v',  // Verdadero
            p7: 'b',  // 12 aristas
            p8: 'v',  // Verdadero
            p9: 'b',  // No tiene caras, v√©rtices ni aristas
            p10: 'v'  // Verdadero
        };

        function calificarEvaluacion() {
            let correctas = 0;
            let totalPreguntas = 10;

            // Verificar cada respuesta
            for (let i = 1; i <= totalPreguntas; i++) {
                const pregunta = `p${i}`;
                const respuestaSeleccionada = document.querySelector(`input[name="${pregunta}"]:checked`);
                const tarjetaPregunta = document.querySelectorAll('.pregunta-card')[i-1];

                if (respuestaSeleccionada) {
                    if (respuestaSeleccionada.value === respuestasCorrectas[pregunta]) {
                        correctas++;
                        tarjetaPregunta.classList.add('correcta');
                        tarjetaPregunta.classList.remove('incorrecta');
                    } else {
                        tarjetaPregunta.classList.add('incorrecta');
                        tarjetaPregunta.classList.remove('correcta');
                    }
                }
            }

            // Mostrar resultado
            const nota = correctas;
            document.getElementById('notaValor').textContent = nota;

            // Mensaje motivacional seg√∫n la nota
            let mensaje = '';
            if (nota === 10) {
                mensaje = '¬°PERFECTO! üåü Eres un maestro de los cuerpos geom√©tricos. Tu esfuerzo y dedicaci√≥n son admirables. ¬°Sigue brillando as√≠!';
            } else if (nota >= 8) {
                mensaje = '¬°EXCELENTE! üéØ Dominas muy bien el tema. Has demostrado gran comprensi√≥n de los conceptos. ¬°Est√°s en el camino correcto!';
            } else if (nota >= 6) {
                mensaje = '¬°MUY BIEN! üëè Vas por buen camino. Has aprendido mucho y con un poco m√°s de pr√°ctica ser√°s experto. ¬°No te rindas!';
            } else if (nota >= 4) {
                mensaje = '¬°BUEN INTENTO! üí™ Est√°s aprendiendo paso a paso. Revisa los conceptos y vuelve a intentarlo. Cada error es una oportunidad para crecer. ¬°T√∫ puedes!';
            } else {
                mensaje = '¬°SIGUE ADELANTE! üå± El aprendizaje es un proceso y cada intento te acerca m√°s al √©xito. Repasa los conceptos con calma y vuelve a intentarlo. ¬°Conf√≠o en ti!';
            }

            document.getElementById('mensajeMotivacional').textContent = mensaje;
            document.getElementById('resultadoFinal').classList.add('show');

            // Scroll suave al resultado
            document.getElementById('resultadoFinal').scrollIntoView({ behavior: 'smooth', block: 'center' });
        }

        function reiniciarEvaluacion() {
            // Limpiar todas las selecciones
            const radios = document.querySelectorAll('input[type="radio"]');
            radios.forEach(radio => radio.checked = false);

            // Quitar clases de correcto/incorrecto
            const tarjetas = document.querySelectorAll('.pregunta-card');
            tarjetas.forEach(tarjeta => {
                tarjeta.classList.remove('correcta', 'incorrecta');
            });

            // Ocultar resultado
            document.getElementById('resultadoFinal').classList.remove('show');

            // Scroll al inicio de la evaluaci√≥n
            document.querySelector('.evaluacion-section').scrollIntoView({ behavior: 'smooth' });
        }
